package com.datatypes;

public class IntDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		byte marks = 98;
		
		System.out.println("My maths marks are " +marks);
		
		short total_money = 150;
		
		System.out.println("My maths marks are " +total_money);
		
		int mymobile = 98989898;
		
		System.out.println("My new number is " +mymobile);
		
		// Type casting
		float my_marks = (float) 60.89;
		
		System.out.println(my_marks);
		
		float my_maths_marks = 89.45f;
		
		System.out.println(my_maths_marks);
		
		double my_new_maths = 4545.78;
		
		System.out.println(my_new_maths);
		
		//down casting //int a = 45.45;
		
		int a = (int) 45.45;
		
		System.out.println(a);
		
		
	}

}
